content.value:
body {
  background: #fafafa;
  color: #222;
}

.btn { color: #fff; }
#main { padding: 2rem; }
nav ul > li.active { font-weight: bold; }

.card {
  width: 300px;
  padding: 1rem;
  border: 2px solid #ffc107;
  margin: 1rem auto;
}

h1 { color: #111827; font-size: 2.5rem; }
p { color: #374151; line-height: 1.7; }
.important { color: #dc3545; font-weight: bold; }

@media (max-width: 600px) {
  .container { padding: 0.5rem; }
}
